import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import {
    Badge, Button,
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    NavbarText
  } from 'reactstrap';
import { connect } from 'react-redux';
import { authLogout } from '../action';

class NambarComp extends Component {
    constructor(props) {
        super(props);
        this.state = {  
            isOpen: false,
            cart: 0
        }
    }

    componentDidMount() {
        this.countCart()
    }

    toggle = () => {
        this.setState({ isOpen: !this.state.open })
    }

